mapper.select:
select



from skus t where 1=1

    and sku_type=#{skuType}


    and sku_type_id=#{skuTypeId}


    and inv_id=#{invId}


    and sku_type_status=#{skuTypeStatus}

select



from themes t,theme_cate tc
where 1=1 and t.end_at >=CURRENT_TIMESTAMP(0) and t.or_destroy=false


    and t.theme_state = #{themeState}


    and tc.theme_cate_code = #{themeCateCode}


and t.id=tc.theme_id

ORDER BY t.sort_nu desc

    LIMIT #{pageSize}


    OFFSET #{offset}

select
from themes t where 1=1
and t.id = #{id}

select



from slider t where 1=1

    and t.id = #{id}


    and t.or_nav = #{orNav}


    and t.slider_type = #{sliderType}

ORDER BY t.sort_nu

select



from inventories t
where 1=1

    and t.item_id = #{itemId}


    and t.id = #{id}


    and t.or_master_inv = #{orMasterInv}

select



from versioning t where 1=1

    and product_type=#{productType}


    and id=#{id}

order by current_version DESC,release_number DESC

select



from nav_item_cate t where 1=1 and or_destroy=false

    and nav_id=#{navId}

select



from skus t where 1!=1


    union (
    select



    from skus t  where 1=1 and t.or_master_inv=true and t.sku_type='item'

        t.item_id=#{itemId}

    )



    union (
    select



    from skus t  where 1=1 and t.sku_type='item'

        t.sku_type_id=#{invId}

    )



    union (
    select



    from skus t  where 1=1 and t.or_master_inv=true and t.sku_type='pin'

        t.sku_type_id=#{pinId}

    )



    union (
    select



    from skus t ,items it where 1=1 and t.or_master_inv=true and t.item_id=it.id and

        it.cate_id=#{cateId}

    )



    union (
    select



    from skus t  where 1=1 and t.or_master_inv=true and

        t.themeId like '%${themeId}%'

    )


ORDER BY create_at DESC

    LIMIT #{pageSize}


    OFFSET #{offset}

select



from skus t where 1=1 and sku_type_status='Y'

    and sku_type=#{skuType}


    and sku_type_id=#{skuTypeId}


    and inv_id=#{invId}


    and sku_type_status=#{skuTypeStatus}


order by sku_type_sold_amount desc


    limit #{limitNum}

select



from cates t where 1=1

    AND pcate_id = #{pcateId}


    AND cate_id = #{cateId}

ORDER BY t.cate_nm;
